$(function() {
	
$("#sendmail").submit( function(event) {

event.preventDefault();

$("#message").attr("class", "sending");

var $form = $(this),
	from = $form.find("input[name='from']").val(),
	subject  = $form.find("input[name='subject']").val(),
	message = $form.find("textarea").val(),
	url = $form.attr("action");

var posting = $.post (url, {from: from, subject: subject, message: message});

posting.done(function(data){
if(data.match("^Error")){   $("#message").attr("class", "error");   }
else{   $("#message").attr("class", "ok");   }
$("#message").html(data);
})
	.fail(function(data){$("#message").attr("class", "error").html("Could not send request. Try again");});


});
});